mapper.select:
select id, date(start_time) as start_time, end_time, time_flag, booking_pid,application_id, booking_person,
booking_phone, create_time, instrument_id
from booking

    AND start_time >= #{starttime,jdbcType=TIMESTAMP}
    AND start_time <= #{endtime,jdbcType=TIMESTAMP}
    AND instrument_id = #{instrumentId,jdbcType=INTEGER}

select id, start_time, end_time, time_flag, booking_pid,application_id, booking_person, booking_phone,
create_time, instrument_id
from booking

   AND start_time >= #{starttime,jdbcType=TIMESTAMP}
  AND start_time <= #{endtime,jdbcType=TIMESTAMP}

ORDER BY start_time ,instrument_id ASC
