mapper.select:
SELECT i.id, i.name, i.sn, e.msg, e.occur_time, e.status
FROM instrument_event e
INNER JOIN instrument i ON e.instrument_id = i.id
WHERE i.id = #{instrumentId,jdbcType=BIGINT}
AND e.create_time >= #{startTime,jdbcType=TIMESTAMP}
AND e.create_time <= #{endTime,jdbcType=TIMESTAMP} 
AND e.level >= #{level,jdbcType=INTEGER}
ORDER BY e.occur_time DESC
limit #{start,jdbcType=INTEGER}, #{size,jdbcType=INTEGER}

SELECT count(1) FROM instrument_event e
INNER JOIN instrument i ON e.instrument_id = i.id
WHERE i.id = #{instrumentId,jdbcType=BIGINT}
AND e.create_time >= #{startTime,jdbcType=TIMESTAMP}
AND e.create_time <= #{endTime,jdbcType=TIMESTAMP} 
AND e.level >= #{level,jdbcType=INTEGER}
