mapper.insert:

insert into instrument (id, type, name, 
  sn, in_use, last_heartbreak, 
  disabled, create_by, update_by, 
  create_time, update_time, description,status
  )
values (#{id,jdbcType=BIGINT}, #{type,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{sn,jdbcType=VARCHAR},0 , #{lastHeartbreak,jdbcType=BIGINT},
  #{disabled,jdbcType=BIT}, #{createBy,jdbcType=BIGINT}, #{createBy,jdbcType=BIGINT},
  now(), now(), #{description,jdbcType=LONGVARCHAR},#{status,jdbcType=INTEGER}
)

mapper.select:
  SELECT id, type, name, sn, in_use, last_heartbreak, disabled, create_by, update_by,
create_time, update_time, description,status
FROM instrument

  and status = #{status,jdbcType=INTEGER}
  and type = #{type,jdbcType=INTEGER}

limit #{start,jdbcType=INTEGER}, #{size,jdbcType=INTEGER}

SELECT count(1) FROM instrument

  and status = #{status,jdbcType=INTEGER}
  and type = #{type,jdbcType=INTEGER}
