mapper.insert:

insert into microscope_tracking (id, start_time, end_time, 
  booking_pid, microscope_id, microscope
  )
values (#{id,jdbcType=BIGINT}, #{startTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}, 
  #{bookingPid,jdbcType=BIGINT}, #{microscopeId,jdbcType=INTEGER}, #{microscope,jdbcType=VARCHAR}
  )

mapper.select:

select id, start_time, end_time, booking_pid, microscope_id, microscope
from microscope_tracking
where id = #{id,jdbcType=BIGINT}

select a.id, a.start_time as startTime, a.end_time as endTime, a.booking_pid as bookingPid, a.microscope_id as microscopeId,
a.microscope, b.first_name as firstName, b.wno, b.phone, b.departments
from microscope_tracking a
inner join user b on a.booking_pid = b.id

  a.microscope_id = #{microscopeId,jdbcType=INTEGER}
  AND a.start_time >= #{startTime,jdbcType=TIMESTAMP}
  and a.start_time <= #{endTime,jdbcType=TIMESTAMP}

select count(1)
from microscope_tracking

  microscope_id = #{microscopeId,jdbcType=INTEGER}
  AND start_time >= #{startTime,jdbcType=TIMESTAMP}
  and start_time <= #{endTime,jdbcType=TIMESTAMP}
